css:
.itemTable {
    border: 1px solid black;
    width: 100%;
    border-collapse: collapse;
    border-top: none !important;
    font-size: 12px;
}

.itemTable th {
    border: 1px solid black;
    color: black;
    text-align: center;
    font-weight: bold;
    background-color: #bebebe !important;
}

.itemTable td {
    text-align: center;
    border-left: 1px solid black;
}

.itemTable .borderTop {
    border-top: 1px solid black;
}

.itemTable .totalRow {
    border-top: 1px solid black;
    font-weight: 600;
    border-bottom: 1px solid black;
}

.totalAmountInWordsTable {
    width: 100%;
    border: 1px solid black;
    border-top: none;
    border-bottom: none;
    font-size: 12px;
}

.spacerRow td {
    height: 30px;
}

.signTable {
    border: 1px solid black;
    border-top: 0px solid black;
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
}

.signTable td {
    border-right: 1px solid black;
    /*border-left: 1px solid black;*/
}

.signTable .alignRight {
    text-align: end;
    border-left: 1px solid black;
    /*border-top: 1px solid black;*/
    width: 50%;
}

.panTable .spacerRow td {
    height: 100px;
}

.endLines {
    text-align: center;
}

.taxTable {
    border: 1px solid black;
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
    border-top: 1px solid black;
}

.taxTable th {
    border: 1px solid black;
    color: black;
    text-align: center;
    font-weight: bold;
    background-color: #bebebe !important;
}

.taxTable td {
    text-align: center;
    border-left: 1px solid black;
}

.taxTable .totalRow {
    border-top: 1px solid black;
    font-weight: 600;
    border-bottom: 1px solid black;
}

.panTable {
    border-left: 1px solid black;
    border-right: 1px solid black;
    width: 100%;
    font-size: 12px;
}
